mapper.update:

update sc_multipart_file
set multipart_type = #{multipartType,jdbcType=TINYINT},
  multipart_from = #{multipartFrom,jdbcType=TINYINT},
  create_on = #{createOn,jdbcType=TIMESTAMP},
  relation_id = #{relationId,jdbcType=BIGINT}
where multipart_id = #{multipartId,jdbcType=VARCHAR}

mapper.select:

select multipart_id, multipart_type, multipart_from, create_on, relation_id
from sc_multipart_file
where multipart_id = #{multipartId,jdbcType=VARCHAR}
