mapper.update:

update sc_user_friend
set
remark = #{remark,jdbcType=VARCHAR}
where user_id = #{user_id,jdbcType=BIGINT} and friend_id=#{friend_id,jdbcType=BIGINT}

mapper.select:

select id, user_id, friend_id, status, add_time, update_time, remark, group_id
from sc_user_friend
where id = #{id,jdbcType=BIGINT}

select u.id user_id,u.user_name,f.remark,u.avatar FROM sc_user_friend f
 left join sc_user u on f.friend_id = u.id
 WHERE f.group_id = #{groupId,jdbcType=BIGINT}

select u.id user_id,u.user_name,f.remark,u.avatar FROM sc_user_friend f
left join sc_user u on f.friend_id = u.id
WHERE f.user_id = #{userId,jdbcType=BIGINT}
